mapper.update:
UPDATE member 
SET member_pw=PASSWORD(#{memberPw})
WHERE member_id=#{memberId}
AND member_email=#{memberEmail}

mapper.select:
SELECT *
FROM member
WHERE member_id=#{memberId}
AND member_email=#{memberEmail}

SELECT
	CONCAT(SUBSTRING(member_id,1,3),'****')
FROM member
WHERE member_name=#{memberName}
AND member_phone=#{memberPhone}
AND member_email=#{memberEmail}

mapper.insert:
INSERT INTO member( member_id, member_pw, member_name, member_addr, member_phone, member_email
) VALUES ( #{memberId}, PASSWORD(#{memberPw}), #{memberName}, #{memberAddr}, #{memberPhone}, #{memberEmail}
)
